import React, { useState } from 'react';
import './App.css';

const months = [
  'January', 'February', 'March', 'April', 'May', 'June',
  'July', 'August', 'September', 'October', 'November', 'December'
];

function App() {
  const [currentStep, setCurrentStep] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [apiResponse, setApiResponse] = useState(null);
  const [formData, setFormData] = useState({
    recipient: '',
    age: 18,
    gender: '',
    occasion: '',
    month: '',
    selectedImages: [],
    budget: ''
  });

  const updateFormData = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextStep = () => {
    if (currentStep < 8) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const toggleImageSelection = (index) => {
    const newSelection = formData.selectedImages.includes(index)
      ? formData.selectedImages.filter(i => i !== index)
      : [...formData.selectedImages, index];
    updateFormData('selectedImages', newSelection);
  };

  const submitToAPI = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('https://cjrw1dwlx2.execute-api.us-east-1.amazonaws.com/outfit-bundles', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          recipient: formData.recipient,
          age: formData.age,
          gender: formData.gender,
          occasion: formData.occasion,
          month: formData.month,
          selectedImages: formData.selectedImages,
          budget: formData.budget
        })
      });

      const data = await response.json();
      setApiResponse(data);
      setCurrentStep(9); // Go to results page
    } catch (error) {
      console.error('Error calling API:', error);
      setApiResponse({ error: 'Failed to get gift recommendations. Please try again.' });
      setCurrentStep(9);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app">
      <div className="page active">
        <div className="logo">ALDO</div>
        <div className="mascot">üéÅ</div>
      </div>

      {/* Step 0: Welcome */}
      {currentStep === 0 && (
        <div className="paper-overlay step-0">
          <div className="question">
            So you want to buy a gift?
            <br />
            <br />
            I'm Gifty and let's buy a gift together!
          </div>
          <button className="next-btn" onClick={nextStep}>
            Let's Start!
          </button>
        </div>
      )}

      {/* Step 1: Who is it for? */}
      {currentStep === 1 && (
        <div className="paper-overlay step-1">
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Who is it for?</div>
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter name..."
              value={formData.recipient}
              onChange={(e) => updateFormData('recipient', e.target.value)}
            />
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={!formData.recipient.trim()}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 2: Age */}
      {currentStep === 2 && (
        <div className="paper-overlay step-2">
          <div className="step-number">3</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Tell me about them?</div>
          <div className="age-slider">
            <label>Age:</label>
            <input
              type="range"
              min="1"
              max="100"
              value={formData.age}
              onChange={(e) => updateFormData('age', parseInt(e.target.value))}
            />
            <div className="age-value">{formData.age}</div>
          </div>
          <button className="next-btn" onClick={nextStep}>
            Next
          </button>
        </div>
      )}

      {/* Step 3: Gender */}
      {currentStep === 3 && (
        <div className="paper-overlay step-3">
          <div className="step-number">4</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Tell me about them?</div>
          <div className="options">
            <button
              className={`option-btn ${formData.gender === 'M' ? 'selected' : ''}`}
              onClick={() => updateFormData('gender', 'M')}
            >
              M
            </button>
            <button
              className={`option-btn ${formData.gender === 'F' ? 'selected' : ''}`}
              onClick={() => updateFormData('gender', 'F')}
            >
              F
            </button>
            <button
              className={`option-btn ${formData.gender === 'Other' ? 'selected' : ''}`}
              onClick={() => updateFormData('gender', 'Other')}
            >
              Other
            </button>
            <button
              className={`option-btn ${formData.gender === 'Prefer not to say' ? 'selected' : ''}`}
              onClick={() => updateFormData('gender', 'Prefer not to say')}
            >
              Prefer not to say
            </button>
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={!formData.gender}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 4: When is the gift for? */}
      {currentStep === 4 && (
        <div className="paper-overlay step-4">
          <div className="step-number">5</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Tell me about them?</div>
          <div className="input-group">
            <label>When is the gift for?</label>
            <MonthDropdown 
              value={formData.month}
              onChange={(month) => updateFormData('month', month)}
            />
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={!formData.month}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 5: Occasion */}
      {currentStep === 5 && (
        <div className="paper-overlay step-5">
          <div className="step-number">6</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Tell me about them?</div>
          <div className="input-group">
            <label>Occasion:</label>
            <input
              type="text"
              placeholder="Birthday, Anniversary, etc..."
              value={formData.occasion}
              onChange={(e) => updateFormData('occasion', e.target.value)}
            />
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={!formData.occasion.trim()}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 6: Picture selection */}
      {currentStep === 6 && (
        <div className="paper-overlay step-6">
          <div className="step-number">7</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">Which picture(s) reminds you of them?</div>
          <div className="image-grid">
            {[0, 1, 2, 3].map(index => (
              <div
                key={index}
                className={`image-placeholder ${formData.selectedImages.includes(index) ? 'selected' : ''}`}
                onClick={() => toggleImageSelection(index)}
              />
            ))}
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={formData.selectedImages.length === 0}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 7: Budget */}
      {currentStep === 7 && (
        <div className="paper-overlay step-7">
          <div className="step-number">8</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="question">What's your budget?</div>
          <div className="input-group">
            <label>Enter budget:</label>
            <input
              type="text"
              placeholder="$50, $100, etc..."
              value={formData.budget}
              onChange={(e) => updateFormData('budget', e.target.value)}
            />
          </div>
          <button 
            className="next-btn" 
            onClick={nextStep}
            disabled={!formData.budget.trim()}
          >
            Next
          </button>
        </div>
      )}

      {/* Step 8: Ready to find gifts */}
      {currentStep === 8 && (
        <div className="paper-overlay step-8">
          <div className="step-number">9</div>
          <button className="back-btn" onClick={prevStep}>‚Üê Back</button>
          <div className="final-message">
            <h2>It seems like we are ready.</h2>
            <p>Let's go find a gift!</p>
            <button 
              className="next-btn find-gifts-btn" 
              onClick={submitToAPI}
              disabled={isLoading}
            >
              {isLoading ? 'Finding Gifts...' : 'Find Gifts!'}
            </button>
          </div>
        </div>
      )}

      {/* Step 9: Results from API */}
      {currentStep === 9 && (
        <div className="paper-overlay step-9">
          <div className="results-page">
            {isLoading ? (
              <div className="loading">
                <div className="spinner"></div>
                <p>Finding the perfect gifts for {formData.recipient}...</p>
              </div>
            ) : apiResponse ? (
              <div className="gift-results">
                <h2>Perfect Gifts for {formData.recipient}!</h2>
                {apiResponse.error ? (
                  <div className="error-message">
                    <p>{apiResponse.error}</p>
                    <button className="next-btn" onClick={() => setCurrentStep(8)}>
                      Try Again
                    </button>
                  </div>
                ) : (
                  <div className="recommendations">
                    {apiResponse.recommendations && apiResponse.recommendations.map((gift, index) => (
                      <div key={index} className="gift-card">
                        <h3>{gift.name}</h3>
                        <p className="price">{gift.price}</p>
                        <p className="description">{gift.description}</p>
                        {gift.link && (
                          <a href={gift.link} target="_blank" rel="noopener noreferrer" className="gift-link">
                            View Product
                          </a>
                        )}
                      </div>
                    ))}
                    {apiResponse.message && (
                      <div className="api-message">
                        <p>{apiResponse.message}</p>
                      </div>
                    )}
                  </div>
                )}
                <button className="next-btn" onClick={() => {
                  setCurrentStep(0);
                  setFormData({
                    recipient: '',
                    age: 18,
                    gender: '',
                    occasion: '',
                    month: '',
                    selectedImages: [],
                    budget: ''
                  });
                  setApiResponse(null);
                }}>
                  Start Over
                </button>
              </div>
            ) : null}
          </div>
        </div>
      )}
    </div>
  );
}

function MonthDropdown({ value, onChange }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="dropdown">
      <button 
        className="dropdown-btn"
        onClick={() => setIsOpen(!isOpen)}
      >
        {value || 'Month'}
      </button>
      {isOpen && (
        <div className="dropdown-content">
          {months.map(month => (
            <div
              key={month}
              className="dropdown-item"
              onClick={() => {
                onChange(month);
                setIsOpen(false);
              }}
            >
              {month}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
