import React, { useState, useEffect } from 'react';
import './App.css';

const months = [
  'January', 'February', 'March', 'April', 'May', 'June',
  'July', 'August', 'September', 'October', 'November', 'December'
];

const MascotComponent = () => (
  <div className="mascot">
    <img src="/mascot.png" alt="Gifty mascot" />
  </div>
);

function App() {
  const [currentStep, setCurrentStep] = useState(0);
  const [animationState, setAnimationState] = useState('entered');
  const [formData, setFormData] = useState({
    recipient: '',
    age: 18,
    gender: '',
    occasion: '',
    month: '',
    selectedImages: [],
    budget: ''
  });

  const updateFormData = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextStep = () => {
    if (currentStep < 8) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const toggleImageSelection = (index) => {
    const newSelection = formData.selectedImages.includes(index)
      ? formData.selectedImages.filter(i => i !== index)
      : [...formData.selectedImages, index];
    updateFormData('selectedImages', newSelection);
  };

  return (
    <div className="app">
      {/* Welcome Page */}
      <div className={`page ${currentStep === 0 ? 'active' : currentStep > 0 ? 'next' : ''}`}>
        <div className="logo">ALDO</div>
        <div className="question">
          So you want to buy a gift?
          <br />
          <br />
          I'm Gifty and let's buy a gift together!
        </div>
        <MascotComponent />
        <button className="next-btn" onClick={nextStep}>
          Let's Start!
        </button>
      </div>

      {/* Step 1: Who is it for? */}
      <div className={`page ${currentStep === 1 ? 'active' : currentStep > 1 ? 'next' : ''}`}>
        <div className="logo">ALDO</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Who is it for?</div>
        <div className="input-group">
          <input
            type="text"
            placeholder="Enter name..."
            value={formData.recipient}
            onChange={(e) => updateFormData('recipient', e.target.value)}
          />
        </div>
        <MascotComponent />
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={!formData.recipient.trim()}
        >
          Next
        </button>
      </div>

      {/* Step 2: Age */}
      <div className={`page ${currentStep === 2 ? 'active' : currentStep > 2 ? 'next' : ''}`}>
        <div className="step-number">3</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Tell me about them?</div>
        <div className="age-slider">
          <label>Age:</label>
          <input
            type="range"
            min="1"
            max="100"
            value={formData.age}
            onChange={(e) => updateFormData('age', parseInt(e.target.value))}
          />
          <div className="age-value">{formData.age}</div>
        </div>
        <MascotComponent />
        <button className="next-btn" onClick={nextStep}>
          Next
        </button>
      </div>

      {/* Step 3: Gender */}
      <div className={`page ${currentStep === 3 ? 'active' : currentStep > 3 ? 'next' : ''}`}>
        <div className="step-number">4</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Tell me about them?</div>
        <div className="options">
          <button
            className={`option-btn ${formData.gender === 'M' ? 'selected' : ''}`}
            onClick={() => updateFormData('gender', 'M')}
          >
            M
          </button>
          <button
            className={`option-btn ${formData.gender === 'F' ? 'selected' : ''}`}
            onClick={() => updateFormData('gender', 'F')}
          >
            F
          </button>
          <button
            className={`option-btn ${formData.gender === 'Other' ? 'selected' : ''}`}
            onClick={() => updateFormData('gender', 'Other')}
          >
            Other
          </button>
          <button
            className={`option-btn ${formData.gender === 'Prefer not to say' ? 'selected' : ''}`}
            onClick={() => updateFormData('gender', 'Prefer not to say')}
          >
            Prefer not to say
          </button>
        </div>
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={!formData.gender}
        >
          Next
        </button>
      </div>

      {/* Step 4: When is the gift for? */}
      <div className={`page ${currentStep === 4 ? 'active' : currentStep > 4 ? 'next' : ''}`}>
        <div className="step-number">5</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Tell me about them?</div>
        <div className="input-group">
          <label>When is the gift for?</label>
          <MonthDropdown 
            value={formData.month}
            onChange={(month) => updateFormData('month', month)}
          />
        </div>
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={!formData.month}
        >
          Next
        </button>
      </div>

      {/* Step 5: Occasion */}
      <div className={`page ${currentStep === 5 ? 'active' : currentStep > 5 ? 'next' : ''}`}>
        <div className="step-number">6</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Tell me about them?</div>
        <div className="input-group">
          <label>Occasion:</label>
          <input
            type="text"
            placeholder="Birthday, Anniversary, etc..."
            value={formData.occasion}
            onChange={(e) => updateFormData('occasion', e.target.value)}
          />
        </div>
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={!formData.occasion.trim()}
        >
          Next
        </button>
      </div>

      {/* Step 6: Picture selection */}
      <div className={`page ${currentStep === 6 ? 'active' : currentStep > 6 ? 'next' : ''}`}>
        <div className="step-number">7</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">Which picture(s) reminds you of them?</div>
        <div className="image-grid">
          {[0, 1, 2, 3].map(index => (
            <div
              key={index}
              className={`image-placeholder ${formData.selectedImages.includes(index) ? 'selected' : ''}`}
              onClick={() => toggleImageSelection(index)}
            />
          ))}
        </div>
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={formData.selectedImages.length === 0}
        >
          Next
        </button>
      </div>

      {/* Step 7: Budget */}
      <div className={`page ${currentStep === 7 ? 'active' : currentStep > 7 ? 'next' : ''}`}>
        <div className="step-number">8</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="question">What's your budget?</div>
        <div className="input-group">
          <label>Enter budget:</label>
          <input
            type="text"
            placeholder="$50, $100, etc..."
            value={formData.budget}
            onChange={(e) => updateFormData('budget', e.target.value)}
          />
        </div>
        <button 
          className="next-btn" 
          onClick={nextStep}
          disabled={!formData.budget.trim()}
        >
          Next
        </button>
      </div>

      {/* Final Page */}
      <div className={`page ${currentStep === 8 ? 'active' : ''}`}>
        <div className="step-number">9</div>
        {currentStep > 0 && (
          <button className="back-btn" onClick={prevStep}>← Back</button>
        )}
        <div className="final-message">
          <h2>It seems like we are ready.</h2>
          <p>Let's go find a gift!</p>
        </div>
      </div>
    </div>
  );
}

function MonthDropdown({ value, onChange }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="dropdown">
      <button 
        className="dropdown-btn"
        onClick={() => setIsOpen(!isOpen)}
      >
        {value || 'Month'}
      </button>
      {isOpen && (
        <div className="dropdown-content">
          {months.map(month => (
            <div
              key={month}
              className="dropdown-item"
              onClick={() => {
                onChange(month);
                setIsOpen(false);
              }}
            >
              {month}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
